import {
    YEAR_SELECTION,
    SEMESTER_SELECTION,
    GENDER_SELECTION,
    RESULT_SELECTION,
    SEARCH_SUCCESS,
    SEARCH_FALIURE,
    SEARCH
} from '../actions/academicactions/types';

const INITIAL_STATE = {
   year : '',
   semester: '',
   gender: '',
   result: ''
}
export default (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case YEAR_SELECTION:
            //make a new object,take all the properties of al the existing object and put it in the new state
            //define the property email and toss it in the state
            //..state produces a brand new object   
            return { ...state, year: action.payload }
        case SEMESTER_SELECTION:
            return { ...state, semester: action.payload }
        case GENDER_SELECTION:
            return {
                ...state,             
                gender: action.payload
            }

        case RESULT_SELECTION:
            return {
                ...state,
                result:action.payload   
            }

        default:
            return state;
    }

}
